                }
            }, 100);
        }

        // Configuração para cada menu dropdown
        const menus = [
            {
                buttonId: 'account-link',
                menuId: 'meu-perfil-submenu',
                setaBaixoId: 'account-seta-baixo',
                setaCimaId: 'account-seta-cima',
                activeRoutes: ['user.account', 'user.security', 'user.lg.history'],
                activePaths: [
                    'user/account',
                    'user/security',
                    'user/history/login',
                    'conta',
                    'seguranca',
                    'historico/login'
                ]
            },
            {
                buttonId: 'gestao-saldo-link',
                menuId: 'gestao-saldo-submenu',
                setaBaixoId: 'gestao-seta-baixo',
                setaCimaId: 'gestao-seta-cima',
                activeRoutes: ['user.deposits', 'user.saques'],
                activePaths: [
                    'user/history/deposits',
                    'user/history/withdrawals'
                ]
            },
            {
                buttonId: 'historico-apostas-link',
                menuId: 'apostas-submenu',
                setaBaixoId: 'apostas-seta-baixo',
                setaCimaId: 'apostas-seta-cima',
                activeRoutes: ['user.history.casino', 'user.history.sport'],
                activePaths: [
                    'user/history/casino',
                    'user/history/sport',
                    'historico/cassino',
                    'historico/esportes',
                    'history/casino',
                    'history/sport'
                ]
            }
        ];

        // Verificar e abrir os menus ativos inicialmente
        checkActiveMenus();

        // Função para verificar e abrir os menus ativos
        function checkActiveMenus() {
            // Verificar e ativar submenus com base no caminho atual
            menus.forEach(menu => {
                if (shouldSubmenuBeOpen(menu)) {
                    openSubmenu(menu.buttonId, menu.menuId, menu.setaBaixoId, menu.setaCimaId);
                }
            });

            // Verificação específica para ativar o item "Dados da Conta" quando estiver na página correspondente
            const dadosContaLink = document.getElementById('dados-conta-link');
            if (dadosContaLink && (currentRoute === 'user.account' || currentPath.includes('/account') || currentPath.includes('/conta'))) {
                dadosContaLink.classList.add('router-link-active', 'router-link-exact-active', 'jIZao');
                // Assegurar que o submenu está aberto
                openSubmenu('account-link', 'meu-perfil-submenu', 'account-seta-baixo', 'account-seta-cima');
            }

            // Verificação específica para ativar o item "Login e Segurança" quando estiver na página correspondente
            const securityContaLink = document.getElementById('security-conta-link');
            if (securityContaLink && (currentRoute === 'user.security' || currentPath.includes('/security') || currentPath.includes('/seguranca'))) {
                securityContaLink.classList.add('router-link-active', 'router-link-exact-active', 'jIZao');
                // Assegurar que o submenu está aberto
                openSubmenu('account-link', 'meu-perfil-submenu', 'account-seta-baixo', 'account-seta-cima');
            }

            // Verificação específica para ativar os itens do submenu "Histórico de Apostas"
            const historicoCassinoLink = document.getElementById('historico-cassino-link');
            if (historicoCassinoLink && (currentRoute === 'user.history.casino' || currentPath.includes('/cassino') || currentPath.includes('/casino'))) {
                historicoCassinoLink.classList.add('router-link-active', 'router-link-exact-active', 'jIZao');
                // Assegurar que o submenu está aberto
                openSubmenu('historico-apostas-link', 'apostas-submenu', 'apostas-seta-baixo', 'apostas-seta-cima');
            }

            const historicoEsportesLink = document.getElementById('historico-geral-link');
            if (historicoEsportesLink && (currentRoute === 'user.history.sport' || currentPath.includes('/esportes') || currentPath.includes('/sport'))) {
                historicoEsportesLink.classList.add('router-link-active', 'router-link-exact-active', 'jIZao');
                // Assegurar que o submenu está aberto
                openSubmenu('historico-apostas-link', 'apostas-submenu', 'apostas-seta-baixo', 'apostas-seta-cima');
            }
            
            // Verificação específica para ativar o item "Histórico de Login" quando estiver na página correspondente
            const historicoLoginLink = document.getElementById('historico-login-link');
            if (historicoLoginLink && (currentRoute === 'user.lg.history' || currentPath.includes('/history/login') || currentPath.includes('/historico/login'))) {
                historicoLoginLink.classList.add('router-link-active', 'router-link-exact-active', 'jIZao');
                // Assegurar que o submenu está aberto
                openSubmenu('account-link', 'meu-perfil-submenu', 'account-seta-baixo', 'account-seta-cima');
            }
        }

        // Função para abrir um submenu
        function openSubmenu(buttonId, menuId, setaBaixoId, setaCimaId) {
            const submenu = document.getElementById(menuId);
            const setaBaixo = document.getElementById(setaBaixoId);
            const setaCima = document.getElementById(setaCimaId);
            const button = document.getElementById(buttonId);

            if (submenu && setaBaixo && setaCima && button) {
                submenu.style.display = 'block';
                setaBaixo.style.display = 'none';
                setaCima.style.display = 'block';
                button.classList.add('jIZao', 'router-link-active');
            }
        }

        // Função para fechar todos os submenus
        function closeAllSubmenus() {
            menus.forEach(m => {
                const submenu = document.getElementById(m.menuId);
                const setaBaixo = document.getElementById(m.setaBaixoId);
                const setaCima = document.getElementById(m.setaCimaId);

                if (submenu) submenu.style.display = 'none';
                if (setaBaixo) setaBaixo.style.display = 'block';
                if (setaCima) setaCima.style.display = 'none';
            });
        }

        // Função para verificar se um submenu deve estar aberto
        function shouldSubmenuBeOpen(menu) {
            // Verificação específica para Histórico de Apostas
            if (menu.buttonId === 'historico-apostas-link') {
                if (currentPath.includes('/cassino') ||
                    currentPath.includes('/casino') ||
                    currentPath.includes('/esportes') ||
                    currentPath.includes('/sport') ||
                    currentRoute === 'user.history.casino' ||
                    currentRoute === 'user.history.sport') {
                    return true;
                }
            }

            // Verificar rotas nomeadas, se configuradas
            if (menu.activeRoutes) {
                if (menu.activeRoutes.includes(currentRoute)) {
                    return true;
                }
            }

            // Verificar caminhos de URL, se configurados
            if (menu.activePaths) {
                // Remove barras iniciais e finais para normalizar o path atual
                const normalizedCurrentPath = currentPath.replace(/^\/|\/$/g, '').toLowerCase();

                for (const path of menu.activePaths) {
                    // Ignora caminhos vazios
                    if (!path) continue;

                    // Normaliza o caminho do menu
                    const normalizedPath = path.toLowerCase();

                    // Verificação direta do caminho
                    if (normalizedCurrentPath === normalizedPath ||
                        normalizedCurrentPath.startsWith(normalizedPath + '/') ||
                        normalizedCurrentPath.endsWith('/' + normalizedPath) ||
                        normalizedCurrentPath.includes('/' + normalizedPath + '/')) {
                        return true;
                    }
                }
            }

            // Verificações específicas para cada menu
            if (menu.buttonId === 'account-link' &&
                (currentPath.includes('/account') || currentPath.includes('/security') ||
                 currentPath.includes('/conta') || currentPath.includes('/seguranca'))) {
                return true;
            }

            return false;
        }

        // Eventos de clique para cada botão de menu
        const accountButton = document.getElementById('account-link');
        const gestaoSaldoButton = document.getElementById('gestao-saldo-link');
        const historicoApostasButton = document.getElementById('historico-apostas-link');

        if (accountButton) {
            accountButton.addEventListener('click', function(e) {
                e.preventDefault();
                e.stopPropagation();
                toggleSubmenu('account-link', 'meu-perfil-submenu', 'account-seta-baixo', 'account-seta-cima');
            });
        }

        if (gestaoSaldoButton) {
            gestaoSaldoButton.addEventListener('click', function(e) {
                e.preventDefault();
                e.stopPropagation();
                toggleSubmenu('gestao-saldo-link', 'gestao-saldo-submenu', 'gestao-seta-baixo', 'gestao-seta-cima');
            });
        }

        // Adicionar evento de clique melhorado para o botão Histórico de Apostas
        if (historicoApostasButton) {
            historicoApostasButton.addEventListener('click', function(e) {
                e.preventDefault();
                e.stopPropagation();

                // Obtém o submenu e verifica se está visível
                const submenu = document.getElementById('apostas-submenu');
                const isVisible = window.getComputedStyle(submenu).display !== 'none';

                // Fecha todos os submenus
                closeAllSubmenus();

                // Se estava fechado, abre-o
                if (!isVisible) {
                    document.getElementById('apostas-submenu').style.display = 'block';
                    document.getElementById('apostas-seta-baixo').style.display = 'none';
                    document.getElementById('apostas-seta-cima').style.display = 'block';
                    historicoApostasButton.classList.add('jIZao', 'router-link-active');
                }
            });
        }

        // Função para alternar o estado de um submenu (abrir/fechar)
        function toggleSubmenu(buttonId, menuId, setaBaixoId, setaCimaId) {
            const submenu = document.getElementById(menuId);
            const setaBaixo = document.getElementById(setaBaixoId);
            const setaCima = document.getElementById(setaCimaId);

            if (submenu && setaBaixo && setaCima) {
                // Verifica se o submenu está aberto
                const isOpen = window.getComputedStyle(submenu).display !== 'none';

                // Fecha todos os submenus
                closeAllSubmenus();

                // Se o submenu estava fechado, abre-o
                if (!isOpen) {
                    submenu.style.display = 'block';
                    setaBaixo.style.display = 'none';
                    setaCima.style.display = 'block';
                }
            }
        }
    });
</script>